INSERT INTO [BusinessSafe].[dbo].[StatutoryResponsibilityTemplate]([Id], [SectorId],[ResponsibilityCategoryId],[Title],[Description],[GuidanceNote],[ResponsibilityReasonId], [TaskReoccurringTypeId],[CreatedBy],[CreatedOn],[Deleted]) VALUES (10,1,4,'Contractor safety','1.  Review H&S compliance by contractors prior to engaging their services or adding them to a preferred contractors list.  2.  Issue a copy of the Contractor Safety Handbook to each contractor setting the health and safety standards you expect them to adopt.  Obtain receipts.','7-2',1,5, N'4CC5B9B8-E335-4E27-9AA3-C48B9AA6BC4D', '2013-07-23 15:00:00.000', 0)
