INSERT INTO [BusinessSafe].[dbo].[StatutoryResponsibilityTemplate]([Id], [SectorId],[ResponsibilityCategoryId],[Title],[Description],[GuidanceNote],[ResponsibilityReasonId], [TaskReoccurringTypeId],[CreatedBy],[CreatedOn],[Deleted]) VALUES (21,1,5,'Mezzanine floor safety check',NULL,'4-34',1,NULL, N'4CC5B9B8-E335-4E27-9AA3-C48B9AA6BC4D', '2013-07-23 15:00:00.000', 0)
INSERT INTO [BusinessSafe].[dbo].[StatutoryResponsibilityTemplate]([Id], [SectorId],[ResponsibilityCategoryId],[Title],[Description],[GuidanceNote],[ResponsibilityReasonId], [TaskReoccurringTypeId],[CreatedBy],[CreatedOn],[Deleted]) VALUES (22,1,6,'Fixed electrical installation','Check that the condition of the fixed electrical installation remains safe for use by way of an inspection and test carried out by a competent electrical contractor. The normal interval for most premises is 5 years but higher risk situations require more frequent checks.','4-1',1,9, N'4CC5B9B8-E335-4E27-9AA3-C48B9AA6BC4D', '2013-07-23 15:00:00.000', 0)
