/*
SELECT nc.* ,qr.ReportLetterStatement,qr.ReportLetterStatementCategoryId, qr.QuestionId
FROM @AreaOfNonCompliance as nc
	INNER JOIN dbo.SafeCheckQuestionResponse AS qr ON nc.Id = qr.Id
WHERE nc.ReportLetterStatement <> ISNULL(qr.ReportLetterStatement,'')
*/

SET XACT_ABORT ON
BEGIN TRANSACTION



UPDATE SafeCheckQuestionResponse
SET ReportLetterStatement = nc.ReportLetterStatement
FROM @AreaOfNonCompliance as nc
	INNER JOIN dbo.SafeCheckQuestionResponse AS qr ON nc.Id = qr.Id
WHERE nc.ReportLetterStatement  <> ISNULL(qr.ReportLetterStatement,'')


COMMIT TRANSACTION
