      ,[Filename]
      ,[Extension]
      ,[FilesizeByte]
      ,[Title]
      ,[Description]
      ,[DocumentTypeId]
      ,[SiteId]
      ,[Deleted]
      ,[CreatedOn]
      ,[CreatedBy]
